select.if:
SELECT count(DISTINCT bg.TagID) count,bg.SourceAirport countType, DATE_FORMAT(bg.FlightDate,'%Y-%m') time from baggage bg WHERE bg.FlightDate BETWEEN DATE_FORMAT(now(),'%Y') AND now() AND bg.IsDeleted != 1 AND SourceAirport IN
#{airport}
GROUP BY countType,time

SELECT count(DISTINCT bg.TagID) count,bg.SourceAirport countType, DATE_FORMAT(bg.FlightDate,'%Y-%m-%d') time from baggage bg WHERE left (bg.FlightDate,7) = #{time} AND bg.IsDeleted != 1 AND SourceAirport IN
#{airport}
GROUP BY countType,time

mapper.select:
SELECT count(DISTINCT bg.TagID) count,bg.SpecialBaggageType countType,bg.SourceAirport airport,DATE_FORMAT(bg.FlightDate,'%Y-%m') time from baggage bg WHERE bg.FlightDate BETWEEN DATE_FORMAT(now(),'%Y') AND now() AND bg.IsDeleted != 1 AND SourceAirport IN
#{airport}
AND bg.FlightDate BETWEEN DATE_FORMAT(now(),'%Y') AND now() AND bg.IsDeleted != 1 GROUP BY countType,airport,time

select.if:
SELECT count(DISTINCT bg.TagID) count,left(bg.FlightNo,2) countType, DATE_FORMAT(bg.FlightDate,'%Y-%m') time from baggage bg WHERE bg.FlightDate BETWEEN DATE_FORMAT(now(),'%Y') AND now() AND bg.IsDeleted != 1 AND left(bg.FlightNo,2) IN
#{company}
GROUP BY countType,time

SELECT count(DISTINCT bg.TagID) count,left(bg.FlightNo,2) countType, DATE_FORMAT(bg.FlightDate,'%Y-%m-%d') time from baggage bg WHERE left (bg.FlightDate,7) = #{time} AND bg.IsDeleted != 1 AND left(bg.FlightNo,2) IN
#{company}
GROUP BY countType,time

mapper.select:
SELECT count(DISTINCT bg.TagID) count,bg.SpecialBaggageType countType,left(bg.FlightNo,2) company,DATE_FORMAT(bg.FlightDate,'%Y-%m') time from baggage bg WHERE bg.FlightDate BETWEEN DATE_FORMAT(now(),'%Y') AND now() AND bg.IsDeleted != 1 AND left(bg.FlightNo,2) in
#{company}
GROUP BY countType,company,time
